import { useState } from 'react'

const Contact = ({ name }) => <p>{name}</p>

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [newName, setNewName] = useState('')

  const handleInputChange = (event) => {
    setNewName(event.target.value);
  }

  const createPersonObject = () => {
    const personObject = {
      name: newName
    }

    return personObject
  }

  const addNewPerson = (event) => {
    event.preventDefault();

    const personExistsAlready = doesPersonExist()
    alert(`${newName} is already added to phonebook`)

    if (personExistsAlready) {
      alert('person exists')
    } else {
      const personObject = createPersonObject();

      setPersons(persons.concat(personObject))
    } 


  }


  const doesPersonExist = () => {
    const fakeNewPerson = createPersonObject()

    if ([...persons].some(person => person.name == fakeNewPerson.name)) {
      return true;
    }

    return false;
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addNewPerson}>
        <div>
          name: <input value={newName} onChange={handleInputChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>

      {persons.map(person => <Contact key={person.name} name={person.name} />)}

    </div>
  )

}

export default App
