import { useEffect, useState } from 'react'
import axios from 'axios'

import persons from './services/persons'

const Contact = ({ person }) => <p>{person.name} {person.number}</p>

const Filter = ({ inputValue, onInputChange }) => (
  <div>
  filter shown with <input value={inputValue} onChange={onInputChange} />
</div>
)

const App = () => {

  const [persons, setPersons] = useState([])

  const getPersons = () => {
    persons.getAllPersons()
      .then(response => {
        setPersons(response.data)
      })
  }

  useEffect(getPersons, [])

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  const handleInputChange = (event) => {
    setNewName(event.target.value);
  }

  const handleNumberChenge = (event) => {
    setNewNumber(event.target.value);
  }

  const filterBy = (event) => {
    setFilter(event.target.value);
  }

  const createPersonObject = () => {
    const personObject = {
      name: newName,
      number: newNumber
    }

    return personObject
  }

  const personsToShow = filter === '' 
  ? persons
  : persons.filter(person => person.name.toLowerCase().includes(filter.toLowerCase()))

  const addNewPerson = (event) => {
    event.preventDefault();

    const personExistsAlready = doesPersonExist()

    if (personExistsAlready) {
      alert(`${newName} is already added to phonebook`)
    } else {
      const personObject = createPersonObject();

      axios.post('http://localhost:3001/persons', personObject)
      .then(response => {
        console.log('posted', response)
      })
      setPersons(persons.concat(personObject))
    } 


  }



  const doesPersonExist = () => {
    const fakeNewPerson = createPersonObject()

    if ([...persons].some(person => person.name === fakeNewPerson.name)) {
      return true;
    }

    return false;
  }


  return (
    <div>
      <h2>Phonebook</h2>

      <Filter inputValue={filter} onInputChange={filterBy} />

      <h2>add a new</h2>
      <form onSubmit={addNewPerson}>
        <div>
          name: <input value={newName} onChange={handleInputChange} />
        </div>

        <div>number: <input value={newNumber} onChange={handleNumberChenge} /></div>

        <div>
          <button type="submit">add</button>
        </div>

      </form>
      <h2>Numbers</h2>

      {personsToShow.map(person => <Contact key={person.name} person={person} />)}

    </div>
  )

}

export default App
