import axios from "axios";
const baseUrl = 'http://localhost:3001/persons'

const getAllPersons = () => {
    return axios.get(baseUrl)
}

const newPerson = personObj => {
    return axios.post(baseUrl, personObj)
}

export default {
    getAllPersons: getAllPersons,
    newPerson: newPerson
}
